function inIframe(){try{return window.self!==window.top}catch(e){return!0}}function createExpandCollapseMarkup(element){var id=$(element).attr("id");$(element).addClass("expand-collapse expand-collapse--white").attr("data-id",id).attr("data-caption","YaaS Quick Setup").removeAttr("id")}function addExpandCollapse(next){setScriptTag(expandCollapseScript,function(){buildExpandCollapse(window.document,next),$(".code-toggle").unbind("click").click(function(e){e.preventDefault(),$(this).toggleClass("open-circle"),$(this).parent().find(".expand-collapse").slideToggle(300,function(){dispatchResizeEvent()}),$(this).find(".hyicon-expand, .hyicon-collapse").toggle()})})}if(inIframe())var expandCollapseScript="/scripts/general/expand-collapse.js";else var expandCollapseScript="https://devportal.yaas.io/scripts/general/expand-collapse.js";